suite('create')

var create  = require('../').create
  , a       = require('assert')

test('create', function(){

    var proto = {}
      , mixin1= { x: 1, y: 1 }
      , mixin2= { y: 2 } 
      , o     = create(proto, mixin1, mixin2)

    a.equal(Object.getPrototypeOf(o), proto)
    a.equal(o.x, mixin1.x)
    a.equal(o.y, mixin2.y)
})
