suite('extend')

var extend  = require('../').extend
  , a       = require('assert')

test('extend', function(){
    
    var o1  = { x: 1, y: 1, z: 1 }
      , o2  = { y: 2, z: 2 }
      , o3  = { z: 3 }

    var ret = extend(o1, o2, o3)

    a.equal(ret, o1)

    a.equal(ret.x, 1)
    a.equal(ret.y, 2)
    a.equal(ret.z, 3)
})
